<?php session_start();require_once 'config.php';if($_SERVER['REQUEST_METHOD']==='POST'){$username=trim($_POST['username']?? '');$password=$_POST['password']?? '';if(!empty($username)&&!empty($password)){$stmt=$pdo->prepare("SELECT id, username, password FROM admin_users WHERE username = ?");$stmt->execute([$username]);$admin=$stmt->fetch();if($admin&&password_verify($password,$admin['password'])){$_SESSION['admin_id']=$admin['id'];$_SESSION['admin_username']=$admin['username'];header('Location: admin_panel.php');exit;}else{$error="Invalid username or password";}}else{$error="Please fill in all fields";}} ?><!doctypehtml><html lang="en"><head><meta charset="UTF-8"><meta content="width=device-width,initial-scale=1"name="viewport"><title>Admin Login - DN Tours</title><link href="../css/style.css"rel="stylesheet"></head><body><div class="admin-container"style="max-width:400px;margin:5rem auto"><div class="admin-header"><h1>Admin Login</h1><p>Please login to access the admin panel</p></div><?php if(isset($error)): ?><div class="notification notification-error notification-show"><span class="notification-message"><?php echo htmlspecialchars($error); ?></span></div><?php endif; ?><form class="review-form"method="POST"><div class="form-group"><label for="username">Username</label> <input id="username"name="username"placeholder="Enter username"required></div><div class="form-group"><label for="password">Password</label> <input id="password"name="password"placeholder="Enter password"required type="password"></div><button class="btn-submit"type="submit">Login</button></form></div></body></html>